import React, {useState, useEffect} from "react";
import axios from 'axios'
import QualityCreate from "./QualityCreate"
// import QualityList from "./QualityList"

const InventoryResult = () =>{
    const [inventories, setInventories] = useState({});
    
    const fetchResults = async () => {
        const res = await axios.get('http://localhost:4000/parts')

        setInventories(res.data)
    };
    useEffect(() => {
        fetchResults();
    }, [])

    console.log(inventories)

    const renderedPosts = Object.values(inventories).map((part) => {
        return (
          <div
            className="card"
            style={{ width: "30%", marginBottom: "20px" }}
            key={part.id}
          >
            <div className="card-body">
              <h3 className="card-subtitle mb-2 text-body-secondary">Inventory Item:</h3> <h5 className="card-text">{inventory.title}</h5>
              <h3 className="card-subtitle mb-2 text-body-secondary">Manufacturer:</h3> <h5 className="card-text">{inventory.manufacturer}</h5>
              <h3 className="card-subtitle mb-2 text-body-secondary">Quantity:</h3> <h5 className="card-text">{inventory.quantity}</h5>
            </div>
            <div className="card-body mb-2" >
              <h3>Enter The Quality Report Here</h3>
              {/* <QualityList comments={inventory.comments} /> */}
              <QualityCreate inventoryId={part.id} />
            </div>
          </div>

        );
      });
      return (
        <div className="d-flex flex-row flex-wrap justify-content-between">
          {renderedPosts}
        </div>
      );
    };

export default InventoryResult;
