import React, { useState } from "react";
import axios from "axios";

const QualityCreate = ({ partId }) => {
  const [quality, setQuality] = useState("");

  const onSubmit = async (event) => {
    event.preventDefault();

    await axios.post(`http://localhost:4001/part/${partId}/quality`, {
      quality,
    });

    setQuality("");
  };

  return (
    <div>
      <form onSubmit={onSubmit}>
        <div className="form-group">
          <label>New Quality Report</label>
          <input
            value={quality}
            onChange={(e) => setQuality(e.target.value)}
            className="form-control"
          />
          <label>Rate Part</label>
          {/* <ul class="dropdown-menu">
            <li><a className="dropdown-item">High Quality</a></li>
            <li><a className="dropdown-item" >Moderate Quality</a></li>
            <li><a className="dropdown-item" >Low Quality</a></li>
          </ul> */}
          <div class="btn-group" role="group" aria-label="Basic radio toggle button group">
            <input type="radio" class="btn-check" name="btnradio" id="btnradio1" autocomplete="off" checked />
            <label class="btn btn-outline-primary" for="btnradio1">High Quality</label>

            <input type="radio" class="btn-check" name="btnradio" id="btnradio2" autocomplete="off" />
            <label class="btn btn-outline-primary" for="btnradio2">Moderate Quality</label>

            <input type="radio" class="btn-check" name="btnradio" id="btnradio3" autocomplete="off" />
            <label class="btn btn-outline-primary" for="btnradio3">Low Quality</label>
         </div>
        </div>
        <button className="btn btn-primary">Submit</button>
      </form>
    </div>
  );
};

export default QualityCreate;
